script:
frappe.ui.form.on("Purchase Order", {
	validate: function(frm) {
		material_request = 0;
		$.each(frm.doc.items || [], function(i, d) {
			material_request = d.material_request;
		});
		frm.set_value("tdx_c_solicitud", material_request);
	}
});

cur_frm.add_fetch('codigo', 'comportamiento', 'comportamiento')
cur_frm.add_fetch('codigo', 'tipo_fluido', 'tipo_fluido')
cur_frm.add_fetch('empleado', 'employee_name', 'nombre_empleado')
cur_frm.add_fetch('empleado', 'designation', 'puesto')
function get_products(frm, cdt, cdn) {
	frappe.call({
		type: "GET",
		method: "erpnext.projects.doctype.catalogo_proyecto.catalogo_proyecto.get_catalogo_productos",
		args: {
			project: frm.doc.project
		},
		callback: function(r) {
			if (r.message) {
				cur_frm.set_query("item_code", "items", function(){
					return {
						"filters": [
							["Item", "name", "in", r.message]]
					}
				})
			}
			frm.refresh_field("producto");
		}
	})
};
function get_perforado(frm, cdt, cdn) {
	frappe.call({
		type: "GET",
		method: "erpnext.projects.doctype.valoracion_perforado.valoracion_perforado.get_valoracion_perforado",
		args: {
			project: frm.doc.project
		},
		callback: function(r) {
			if (r.message) {
				cur_frm.set_query("rango", "perforado_valorizado", function(){
					return {
						"filters": [
							["Rango Perforado", "nombre", "in", r.message]]
					}
				})
			}
			frm.refresh_field("rango");
		}
	})
};
function get_perforado_details(frm, cdt, cdn) {
	var d = locals[cdt][cdn];
	frappe.call({
		type: "GET",
		method: "erpnext.projects.doctype.valoracion_perforado.valoracion_perforado.get_rango_info",
		args: {
			project: frm.doc.project,
			rango: d['rango']
		},
		callback: function(r){
			d['medida'] = r.message['medida'];
			d['precio'] = r.message['precio'];
			refresh_field("medida");
			refresh_field("precio");
			refresh_field("perforado_valorizado");
		}
	})
};
function get_products_details(frm, cdt, cdn) {
	var d = locals[cdt][cdn];
	frappe.call({
		type: "GET",
		method: "erpnext.projects.doctype.catalogo_proyecto.catalogo_proyecto.get_producto_info",
		args: {
			project: frm.doc.project,
			producto: d['item_code']
		},
		callback: function(r){
			d['item_name'] = r.message['nombre_proyecto'];
			d['item_group'] = r.message['grupo'];
			d['precio'] = r.message['precio'];
			d['stock_uom'] = r.message['stock_uom'];
			d['brand'] = r.message['brand'];
			d['serial_no'] = r.message['serial_no'];
			refresh_field("item_name");
			refresh_field("item_group");
			refresh_field("stock_uom");
			refresh_field("brand");
			refresh_field("serial_no");
			refresh_field("precio");
			refresh_field("items");
		}
	})
};
function get_metros_perforado(frm, cdt, cdn) {
	if (frm.doc.tdx_c_prfinitur !== undefined && frm.doc.tdx_c_prffintur){
		if (frm.doc.tdx_c_prfinitur < frm.doc.tdx_c_prffintur){
			frappe.model.set_value(cdt, cdn, "tdx_c_prfmt", frm.doc.tdx_c_prffintur - frm.doc.tdx_c_prfinitur)
		}
	}
};
function get_metros_perforado_detalle(frm, cdt, cdn) {
	var d = locals[cdt][cdn];
	if (d['metro_desde'] < d['metro_hasta']){
		d['perforado'] = d['metro_hasta'] - d['metro_desde'];
		refresh_field("perforado");
		refresh_field("perforacion");
	}
}
frappe.ui.form.on("Timesheet", {
	tdx_c_prfinitur: function(frm, cdt, cdn) {
		get_metros_perforado(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Timesheet", {
	tdx_c_prffintur: function(frm, cdt, cdn) {
		get_metros_perforado(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Detalle Perforado", {
	"metro_desde": function(frm, cdt, cdn) {
		get_metros_perforado_detalle(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Detalle Perforado", {
	"metro_hasta": function(frm, cdt, cdn) {
		get_metros_perforado_detalle(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Detalle Perforado", {
	"recuperacion": function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];
		if (d['recuperacion'] >= 0 && d['perforado'] >= 0){
			d['porcentaje_recuperacion'] = d['recuperacion'] / d['perforado'] * 100;
			refresh_field("porcentaje_recuperacion");
			refresh_field("perforacion");
		}
	}
});
frappe.ui.form.on("Timesheet", {
	onload: function(frm, cdt, cdn) {
		frm.toggle_display("billing_details_amounts", true);
		if (frm.doc.total_productos_proyecto === undefined){
			frappe.model.set_value(cdt, cdn, "total_productos_proyecto", 0);
			cur_frm.set_df_property("total_productos_proyecto", "read_only", true);
		}
		if (frm.doc.total_perforado_valorizado === undefined){
			frappe.model.set_value(cdt, cdn, "total_perforado_valorizado", 0);
			cur_frm.set_df_property("total_perforado_valorizado", "read_only", true);
		}
		if (frm.doc.tiempo_finalizacion === undefined){
			frappe.model.set_value(cdt, cdn, "tiempo_finalizacion", 0);
		}
	}
});
frappe.ui.form.on("Timesheet", {
	project: function(frm, cdt, cdn) {
		if (frm.doc.project){
			get_perforado(frm, cdt, cdn);
			get_products(frm, cdt, cdn);
		}
	}
});
frappe.ui.form.on("Detalle Perforado Valorizado", {
	"rango": function(frm, cdt, cdn) {
		if (locals[cdt][cdn]['rango']){
			get_perforado_details(frm, cdt, cdn);
		}		
	}
});
frappe.ui.form.on("Detalle Productos", {
	"item_code": function(frm, cdt, cdn) {
		if (locals[cdt][cdn]['item_code']){
			get_products_details(frm, cdt, cdn);
		}		
	}
});
frappe.ui.form.on("Detalle Perforado Valorizado", {
	"metrado": function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];
		if (d['metrado'] && d['rango']){
			d['total'] = d['precio'] * d['metrado'];
			refresh_field("total");
			refresh_field("perforado_valorizado");
			if (d['total']){
				frappe.model.set_value(d['parenttype'], d['parent'], "total_perforado_valorizado", (locals['Timesheet'][d['parent']]['total_perforado_valorizado'] + d['total']))
			}
		}
		else {
			if (d['total']){
				frappe.model.set_value(d['parenttype'], d['parent'], "total_perforado_valorizado", (locals['Timesheet'][d['parent']]['total_perforado_valorizado'] - d['total']))
			}
		}
	}
});
frappe.ui.form.on("Detalle Productos", {
	"bill": function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];
		if (d['bill']){
			d['qty_bill'] = d['qty'];
			d['precio_total'] = d['precio'] * d['qty'];
			refresh_field("qty_bill");
			refresh_field("precio_total");
			refresh_field("items");
			if (d['precio_total']){
				frappe.model.set_value(d['parenttype'], d['parent'], "total_productos_proyecto", (locals['Timesheet'][d['parent']]['total_productos_proyecto'] + d['precio_total']));
			}
		}
		else {
			if (d['precio_total']){
				frappe.model.set_value(d['parenttype'], d['parent'], "total_productos_proyecto", (locals['Timesheet'][d['parent']]['total_productos_proyecto'] - d['precio_total']));
			}
		}
	}
});
frappe.ui.form.on("Timesheet Detail", {
	"activity_type": function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];
		var tiempo_finalizacion = locals['Timesheet'][d['parent']]['tiempo_finalizacion'];
		if (tiempo_finalizacion != 0) {
			d['from_time'] = tiempo_finalizacion;
			refresh_field("from_time");
			refresh_field("time_logs");
		}
	}
});
frappe.ui.form.on("Timesheet Detail", {
	"to_time": function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];
		var tiempo_finalizacion = locals['Timesheet'][d['parent']]['tiempo_finalizacion'];
		locals['Timesheet'][d['parent']]['tiempo_finalizacion'] = d['to_time'];
		refresh_field("tiempo_finalizacion");
		refresh_field("time_logs");
	}
});

cur_frm.add_fetch('tipo_comprobante', 'codigo_tipo_comprobante', 'codigo_tipo_comprobante');
cur_frm.add_fetch('tipo_operacion', 'codigo_tipos_operacion', 'codigo_tipo_operacion');
function filter_operacion(frm, cdt, cdn){
	cur_frm.set_query("tipo_operacion", function(){
		return {
			"filters": [
				["Tipos de Operaciones", "codigo_tipos_operacion", "in", ["02", "04", "06"]]]
		};
	});
}
function filter_comprobantes(frm, cdt, cdn){
	cur_frm.set_query("tipo_comprobante", function(){
		return {
			"filters": [
				["Tipos de Comprobante", "codigo_tipo_comprobante", "in", ["31"]]]
		};
	});
}
frappe.ui.form.on("Purchase Receipt", {
	onload: function(frm, cdt, cdn){
		filter_operacion(frm, cdt, cdn);
		filter_comprobantes(frm, cdt, cdn);
	}
});

cur_frm.add_fetch('tipo_comprobante', 'codigo_tipo_comprobante', 'codigo_comprobante');
cur_frm.add_fetch('tipo_comprobante_proveedor', 'codigo_tipo_comprobante', 'codigo_comprobante_proveedor');
cur_frm.add_fetch('supplier', 'codigo_tipo_documento', 'codigo_tipo_documento');
cur_frm.add_fetch('supplier', 'nombre_tipo_documento', 'tipo_documento_identidad');
cur_frm.add_fetch('tipo_nota_debito', 'codigo_notas_debito', 'codigo_nota_debito');
frappe.ui.form.on("Purchase Invoice", {
	supplier: function(frm, cdt, cdn) {
		get_document_series(frm, cdt, cdn);
		if (frm.doc.codigo_tipo_documento){
			frappe.call({
				type: "GET",
				method: "nubefact_integration.nubefact_integration.doctype.tipos_de_transaccion_sunat.tipos_de_transaccion_sunat.get_tipo_transaccion_compras",
				args: {
					supplier: frm.doc.supplier
				},
				callback: function(r) {
					frappe.model.set_value(cdt, cdn, "tipo_transaccion_sunat", r.message.descripcion);
					frappe.model.set_value(cdt, cdn, "codigo_transaccion_sunat", r.message.codigo);
				}
			});
		}
		else {
			frappe.model.set_value(cdt, cdn, "tipo_transaccion_sunat", null);
			frappe.model.set_value(cdt, cdn, "codigo_transaccion_sunat", null);
		}
	}
});
frappe.ui.form.on("Purchase Invoice", {
	contingencia: function(frm, cdt, cdn) {
		get_document_series(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Purchase Invoice", {
	tipo_comprobante_proveedor: function(frm, cdt, cdn) {
		get_document_series(frm, cdt, cdn);
	}
});
frappe.ui.form.on('Purchase Invoice', {
    validate: function(frm, cdt, cdn) {
    	if (frm.doc.__islocal != 1){
    		if (frm.doc.is_return == 1){
    			if (frm.doc.estado_sunat == null){
		    		frappe.call({
						method: "nubefact_integration.nubefact_integration.facturacion_electronica.send_document",
						args: {
							'invoice': frm.doc.name,
							'doctype': frm.doc.doctype
						},
						callback: function (data) {
							console.log(data)
							if (data != ""){
								frappe.model.set_value(cdt, cdn, "estado_sunat", (data.message.aceptada_por_sunat) ? ("Aceptado") : ("Rechazado"));
								frappe.model.set_value(cdt, cdn, "respuesta_sunat", data.message.sunat_descripcion);
								frappe.model.set_value(cdt, cdn, "codigo_qr_sunat", data.message.cadena_para_codigo_qr);
								frappe.model.set_value(cdt, cdn, "codigo_barras_sunat", data.message.codigo_de_barras);
								frappe.model.set_value(cdt, cdn, "codigo_hash_sunat", data.message.codigo_hash);
							}
				        }
					})
				}
    		}
    	}
    }
});
frappe.ui.form.on('Purchase Invoice', {
	refresh: function(frm, cdt, cdn) {
		if (frm.doc.codigo_comprobante_proveedor !== null){
			get_document_series(frm, cdt, cdn);
		}		
	}
});
frappe.ui.form.on("Purchase Invoice", {
	is_return: function(frm){
		get_document_series(frm, cdt, cdn);
		cur_frm.set_query("tipo_comprobante_proveedor", function(){
			return {
				"filters": [
					["Tipos de Comprobante", "nombre_tipo_comprobante", "in", ["Boleta de Venta", "Factura"]]
				]
			}
		});
	}
});
function get_document_series(frm, cdt, cdn) {
	frappe.call({
		type: "GET",
		method: "nubefact_integration.nubefact_integration.doctype.configuracion.configuracion.get_doc_serie",
		args: {
			doctype: frm.doc.doctype,
			is_return: frm.doc.is_return ? "1" : "0",
			contingencia: frm.doc.contingencia ? "1" : "0",
			codigo_comprobante: frm.doc.codigo_comprobante_proveedor
		},
		callback: function(r) {
			frappe.model.set_value(cdt, cdn, "tipo_comprobante", r.message.descripcion);
			frappe.model.set_value(cdt, cdn, "codigo_comprobante", r.message.codigo);
			frm.set_df_property("naming_series", "options", r.message.series);
		}
	});
};
frappe.ui.form.on("Sales Invoice", {
	before_cancel: function(frm, cdt, cdn){
		if (frm.doc.codigo_comprobante == "8"){
			if (frappe.datetime.get_day_diff(frm.doc.posting_date, frappe.datetime.get_today()) < 7 && frm.doc.estado_anulacion != "En proceso"){
				return new Promise(function(resolve, reject) {
					frappe.prompt([
						{'fieldname': 'motivo', 'fieldtype': 'Data', 'label': 'Motivo de la cancelacion', 'reqd': 1}  
					],
					function(values){
						resolve(values);
					},
					'Cancelacion de Comprobante',
					'Anular'
					)
				}).then(function (values){
					frappe.validated = false;
					frappe.call({
						method: "nubefact_integration.nubefact_integration.facturacion_electronica.cancel_document",
						args: {
							'invoice': frm.docname,
							'doctype': frm.doctype,
							'motivo': values.motivo
						},
						callback: function (data) {
							console.log(data)
							frappe.msgprint("<b>Esperando respuesta de SUNAT</b>", 'Cancelación');
						}
					})
				});
			}
			else {
				frappe.validated = false;
				frappe.msgprint("<b>Documento no se puede anular o esta en proceso de anulación</b>", 'Cancelación');
			}
		}
	}
});

cur_frm.add_fetch('customer', 'codigo_tipo_documento', 'codigo_tipo_documento');
cur_frm.add_fetch('customer', 'tipo_documento_identidad', 'tipo_documento_identidad');
cur_frm.add_fetch('tipo_transaccion_sunat', 'codigo_tipo_transaccion', 'codigo_transaccion_sunat');
cur_frm.add_fetch('tipo_nota_credito', 'codigo_notas_credito', 'codigo_nota_credito');
function get_document_series(frm, cdt, cdn){
	frappe.call({
		type: "GET",
		method: "nubefact_integration.nubefact_integration.doctype.configuracion.configuracion.get_doc_serie",
		args: {
			doctype: frm.doc.doctype,
			is_return: frm.doc.is_return ? "1" : "0",
			contingencia: frm.doc.contingencia ? "1" : "0",
			codigo_tipo_documento: frm.doc.codigo_tipo_documento
		},
		callback: function(r) {
			frappe.model.set_value(cdt, cdn, "tipo_comprobante", r.message.descripcion);
			frappe.model.set_value(cdt, cdn, "codigo_comprobante", r.message.codigo);
			frm.set_df_property("naming_series", "options", r.message.series);
		}
	});
}
function get_document_transaction(frm, cdt, cdn){
	frappe.call({
		type: "GET",
		method: "nubefact_integration.nubefact_integration.doctype.tipos_de_transaccion_sunat.tipos_de_transaccion_sunat.get_tipo_transaccion",
		args: {
			customer: frm.doc.customer,
			is_return: frm.doc.is_return ? "1" : "0"
		},
		callback: function(r) {
			frappe.model.set_value(cdt, cdn, "tipo_transaccion_sunat", r.message.descripcion);
			frappe.model.set_value(cdt, cdn, "codigo_transaccion_sunat", r.message.codigo);
		}
	});
}
function get_product_anticipo(frm, cdt, cdn) {
	if (frm.doc.codigo_transaccion_sunat == "4") {
		frappe.call({
			type: "GET",
			method: "nubefact_integration.nubefact_integration.doctype.configuracion.configuracion.get_product_anticipo",
			callback: function(r) {
				if (r.message) {
					if (frm.doc.sales_invoice_advance === undefined){
						cur_frm.set_query("item_code", "items", function(){
							return {
								"filters": [
									["Item", "name", "in", r.message]]
							}
						})
					}
					else {
						cur_frm.set_query("item_code", "items", function(){
							return {
								"filters": [
									["Item", "name", "!=", r.message]]
							}
						})
					}
					cur_frm.clear_table("items");
					frm.refresh_field("items");
				}
			}
		})
	}
}
function get_sales_invoice_advance(frm, cdt, cdn) {
	if (frm.doc.codigo_transaccion_sunat == "4") {
		cur_frm.set_query("sales_invoice_advance" , function(){
			return {
				"filters": [
				["Sales Invoice", "codigo_transaccion_sunat", "=", "4"],
				["Sales Invoice", "customer", "=", frm.doc.customer],
				["Sales Invoice", "sales_invoice_advance", "=", ""]]
			}
		})
	}
}
frappe.ui.form.on("Sales Invoice", {
	tipo_transaccion_sunat: function(frm, cdt, cdn){
		get_product_anticipo(frm, cdt, cdn);
		get_sales_invoice_advance(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Sales Invoice", {
	sales_invoice_advance: function(frm, cdt, cdn){
		get_product_anticipo(frm, cdt, cdn);
	}
});
frappe.ui.form.on("Sales Invoice", {
	customer: function(frm, cdt, cdn) {
		if (frm.doc.codigo_tipo_documento){
			get_document_series(frm, cdt, cdn);
			get_document_transaction(frm, cdt, cdn);
		}
		else {
			frappe.model.set_value(cdt, cdn, "tipo_comprobante", null);
			frappe.model.set_value(cdt, cdn, "codigo_comprobante", null);
			frappe.model.set_value(cdt, cdn, "tipo_transaccion_sunat", null);
			frappe.model.set_value(cdt, cdn, "codigo_transaccion_sunat", null);
		}
	}
});
frappe.ui.form.on("Sales Invoice", {
	contingencia: function(frm, cdt, cdn) {
		if (frm.doc.codigo_tipo_documento){
			get_document_series(frm, cdt, cdn);
		}
	}
});
frappe.ui.form.on("Sales Invoice", {
	is_return: function(frm, cdt, cdn) {
		if (frm.doc.codigo_tipo_documento){
			get_document_series(frm, cdt, cdn);
		}
	}	
});
frappe.ui.form.on('Sales Invoice', {
    validate: function(frm, cdt, cdn) {
    	if (frm.doc.__islocal != 1){
    		if (frm.doc.estado_sunat == null || frm.doc.is_return == 1){
	    		frappe.call({
					method: "nubefact_integration.nubefact_integration.facturacion_electronica.send_document",
					args: {
						'invoice': frm.doc.name,
						'doctype': frm.doc.doctype
					},
					callback: function (data) {
						console.log(data)
						if (data != ""){
							frappe.model.set_value(cdt, cdn, "estado_sunat", (data.message.key != "") ? ("Aceptado") : ("Rechazado"));
							frappe.model.set_value(cdt, cdn, "respuesta_sunat", data.message.sunat_descripcion);
							frappe.model.set_value(cdt, cdn, "codigo_qr_sunat", data.message.cadena_para_codigo_qr);
							frappe.model.set_value(cdt, cdn, "codigo_barras_sunat", data.message.codigo_de_barras);
							frappe.model.set_value(cdt, cdn, "codigo_hash_sunat", data.message.codigo_hash);
						}
